SELECT F.CATEGORY, M.MAX_PRICE, F.PRODUCT_NAME FROM(SELECT MAX(PRICE) AS MAX_PRICE, CATEGORY FROM FOOD_PRODUCT GROUP BY CATEGORY) M JOIN FOOD_PRODUCT F ON M.CATEGORY = F.CATEGORY AND M.MAX_PRICE = F.PRICE WHERE M.CATEGORY='과자' OR M.CATEGORY='국' OR M.CATEGORY='김치' OR M.CATEGORY='식용유' ORDER BY M.MAX_PRICE DESC;