SELECT T.REST_ID, I.REST_NAME, I.FOOD_TYPE, I.FAVORITES, I.ADDRESS, T.SCORE FROM (SELECT ROUND(AVG(R.REVIEW_SCORE),2) AS SCORE, R.REST_ID FROM REST_REVIEW R JOIN REST_INFO I ON R.REST_ID=I.REST_ID WHERE I.ADDRESS LIKE '서울%' GROUP BY R.REST_ID) T JOIN REST_INFO I ON T.REST_ID=I.REST_ID ORDER BY T.SCORE DESC,I.FAVORITES DESC;